const container = document.querySelector('.container');
const sizeEl = document.querySelector('.size');
const color = document.querySelector('.color');
const resetBtn = document.querySelector('.btn');
const uploadButton = document.getElementById('upload');

let size = sizeEl.value;
let draw = false;

function populate(size) {
  container.style.setProperty('--size', size);
  for (let i = 0; i < size * size; i++) {
    const div = document.createElement('div');
    div.classList.add('pixel');

    div.addEventListener('mousemove', handleMove);
    div.addEventListener('mousedown', handleDown);
    div.addEventListener('mouseup', handleUp);
    div.addEventListener('touchmove', handleMove);
    div.addEventListener('touchstart', handleDown);
    div.addEventListener('touchend', handleUp);

    function handleMove() {
      if (!draw) return;
      div.style.backgroundColor = color.value;
    }

    function handleDown() {
      draw = true;
      div.style.backgroundColor = color.value;
    }

    function handleUp() {
      draw = false;
    }

    container.appendChild(div);
  }
}

window.addEventListener('mouseup', function () {
  draw = false;
});

window.addEventListener('touchend', function () {
  draw = false;
});

function reset() {
  container.innerHTML = '';
  populate(size);
  uploadButton.classList.remove('green');
}

resetBtn.addEventListener('click', reset);

// Additional touch event listeners for mobile devices
container.addEventListener('touchstart', function (event) {
  if (event.touches.length === 1) {
    // Prevent scrolling on touch devices
    event.preventDefault();
    draw = true;
  }
});

container.addEventListener('touchmove', function (event) {
  event.preventDefault();
  const touches = event.touches;
  for (let i = 0; i < touches.length; i++) {
    const touch = touches[i];
    const target = document.elementFromPoint(touch.clientX, touch.clientY);
    if (target && target.classList.contains('pixel')) {
      target.style.backgroundColor = color.value;
    }
  }
});

populate(size);

function generateRandomString(length) {
  let result = '';
  const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';

  for (let i = 0; i < length; i++) {
    const randomIndex = Math.floor(Math.random() * characters.length);
    result += characters.charAt(randomIndex);
  }

  return result;
}

uploadButton.addEventListener('click', function() {
  const randomString = generateRandomString(10);
  alert("Use code " + randomString + " to get your graphic in game.");
  uploadButton.classList.add('green');
});



